    //     body: JSON.stringify(obj)
    //   })
    //   console.log(res)
    // } catch (e) {
    //     console.log(e);
    // };
  }

  render () {
    return (
      <div className="App">
        <header className="App-header">
          <h1>{this.state.message}</h1>
          <form>
            <input type="text" name="formInput" />
            <button type="submit">Submit</button>
          </form>
        </header>
      </div>
    )
  }
};


export default App;
